    console.log(target.checked, cert);

    if (target.checked) {
      this.dummyList.push(cert);
      console.log(this.dummyList);
    } else {
      let index = this.dummyList.findIndex(x => x.id === cert.id);
      this.dummyList.splice(index, 1);
      console.log(this.dummyList);
    }
  }

  removeCertificate(cert) {
    let index = this.dummyList.findIndex(x => x.id == cert.id);
    this.dummyList.splice(index, 1);
  }

}

